  const phrasesToUse = Array.isArray((brandGuidelines as any)?.phrasesToUse)
    ? ((brandGuidelines as any)?.phrasesToUse as string[]).slice(0, 3)
    : [];
  const phrasesToAvoid = Array.isArray((brandGuidelines as any)?.phrasesToAvoid)
