  await db.oAuthState.create({
    data: {
      userId,
      state,
      platform: "twitter",
      expiresAt: new Date(Date.now() + 1000 * 60 * 10), // 10 minutes
    },
  });

  const redirectUri = `${getBaseUrl()}/settings?platform=twitter`;

  // Construct the Twitter OAuth URL (Twitter OAuth 2.0)
